package javaStudy;

public class Study1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello World");
		String name = "迅";
		int age = 22;
		System.out.println("私の名前は" + name + "です");
		System.out.println("年齢は" + age + "です");
		name = "山田太郎";
		age = 30;
		System.out.println("私の名前は" + name + "です");
		System.out.println("年齢は" + age + "です");
		age += 1;
		System.out.println("年齢は" + age + "です");
		
	}

}
